{"title":"azure-cost-network-live","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega/v5.json",
  "padding": 20,
  "signals": [
    {"name": "$cx", "update": "width / 2"},
    {"name": "$cy", "update": "height / 2"},
    {"name": "$nodeRadius", "value": 5},
    {"name": "$nodeCharge", "value": -9},
    {"name": "$linkDistance", "value": 5},
    {"name": "$static", "value": true},
  ],
  
  "data": [
    {
      "name": "resources",
      url: {
        %context%: true
        "%timefield%": "@timestamp",
        index: metrics*
        body: {
          "aggs": {
    "group": {
      "terms": {
        "field": "azure.resource.group",
        "order": {
          "cost": "desc"
        },
        "size": 20
      },
      "aggs": {
        "name": {
          "terms": {
            "field": "azure.resource.name",
            "order": {
              "cost": "desc"
            }
            "size": 50
          },
          "aggs": {
            "type": {
              "terms": {
                "field": "azure.resource.type",
                "order": {
                  "cost": "desc"
                }
              },
              "aggs": {
                "cost": {
                  "avg": {
                    "field": "azure.billing.pretax_cost"
                  }
                }
              }
            },
            "cost": {
              "avg": {
                "field": "azure.billing.pretax_cost"
              }
            }
          }
        },
        "cost": {
          "avg": {
            "field": "azure.billing.pretax_cost"
          }
        }
      }
    }
  },
  "size": 0
        }
      }
      "format": {"property": "aggregations.group.buckets"},
      "transform": [
        {
          "type": "flatten",
          "fields": ["name.buckets"],
          "as": ["name"]
        }
        {
          "type": "flatten",
          "fields": ["name.type.buckets"],
          "as": ["type"]
        }
        {
          "type": "project",
          "fields": [
            "key",
            "name.key",
            "key",
            "type.key",
            "type.cost.value"
          ],
          "as": ["target", "source", "group","type", "cost"]
        },
        {"type": "formula", "as": "cost", "expr": "datum.cost"}
        ]

    },{ 
    name: parents
    source: resources
    transform: [
      {type: "aggregate", groupby: ["group"], fields: ["cost"], ops: ["sum"]},
      {
          "type": "project",
          "fields": ["group", "group", "group", "sum_cost"],
          "as": ["source", "label", "group", "cost"]
        }
    ]
  },
    {
      "name": "node-data",
      "source": ["parents","resources"],
      "transform": [
            {
          "type": "project",
          "fields": ["source", "type", "label", "cost"],
          "as": ["name", "type", "label", "cost"]
        },
        {
        "type": "window",
        "ops": ["row_number"],
        "as": ["index"]
      },

        {"type": "formula", "as": "index", "expr": "datum['index'] - 1"}
        {"type": "formula", "as": "cost_multiply", "expr": "max(datum['cost']*10,1)"}
      ]
    },
    {
      "name": "link-data",
      "source": "resources",
      "transform": [
        {
          "type": "lookup",
          "from": "node-data",
          "key": "name",
          "fields": ["source", "target"],
          "as": ["source", "target"]
        },
        {"type": "formula", "as": "source", "expr": "datum['source']['index']"},
        {"type": "formula", "as": "target", "expr": "datum['target']['index']"}
      ]
    },
  ],
  "scales": [
    {
      "name": "color",
      "type": "ordinal",
      "reverse": false,
      "domain": {"data": "resources", "field": "type"},
      "range": {"scheme": "dark2"}
    }
  ],
  "legends": [
    {
      "fill": "color",
      "orient": "top-left",
      "encode": {
        "title": {"update": {"fontSize": {"value": 14}}},
        "labels": {
          "interactive": true,
          "update": {"fontSize": {"value": 12}, "fill": {"value": "black"}},
          "hover": {"fill": {"value": "firebrick"}}
        },
        "symbols": {"update": {"stroke": {"value": "transparent"}}},
        "legend": {"update": {}}
      }
    }
  ],
  "marks": [
    {
      "name": "nodes",
      "type": "symbol",
      "zindex": 1,
      "from": {"data": "node-data"},
      "encode": {
        "enter": {
          "fill": {"scale": "color", "field": "type"},
          "stroke": {"value": "white"},
          "tooltip": [
            {"signal": "{'title': 'Node overview','Name':datum.name, 'Type':datum.type, 'Cost (12h)': parseFloat(round(datum.cost*100)/100) + ' $'}"}
          ]
        },
        "update": {
          "size": {"signal": "datum.cost_multiply * $nodeRadius * $nodeRadius"},
          "cursor": {"value": "pointer"}
        }
      },
      "transform": [
        {
          "type": "force",
          "iterations": 300,
          "static": {"signal": "$static"},
          "signal": "force",
          "forces": [
            {"force": "center", "x": {"signal": "$cx"}, "y": {"signal": "$cy"}},
            {"force": "collide", "radius": {"signal": "2 * $nodeRadius"}},
            {"force": "nbody", "strength": {"signal": "$nodeCharge"}},
            {
              "force": "link",
              "links": "link-data"
            }
          ]
        }
      ]
    },
    {
      "type": "path",
      "from": {"data": "link-data"},
      "interactive": false,
      "encode": {
        "update": {
          "stroke": {"value": "#ccc"},
          "strokeWidth": {"value": 2}
        }
      },
      "transform": [
        {
          "type": "linkpath",
          "require": {"signal": "force"},
          "shape": "line",
          "sourceX": "datum.source.x",
          "sourceY": "datum.source.y",
          "targetX": "datum.target.x",
          "targetY": "datum.target.y"
        }
      ]
    },
    {
      "type": "text",
      "zindex": 2,
      "from": {"data": "nodes"},
      "interactive": false,
      "enter": {
        "fill": {"value": "black"},
        "fontSize": {"value": 10}
      },
      "encode": {
        "update": {
          "y": {"field": "y", "offset": {"signal": "($nodeRadius + $linkDistance) * -4"}},
          "x": {"field": "x"},
          "text": {"field": "datum.label"},
          "align": {"value": "center"}
        }
      }
    }
  ]
}"}}

